INSERT INTO `wp_aioseo_notifications` (`id`, `slug`, `title`, `content`, `type`, `level`, `notification_id`, `notification_name`, `start`, `end`, `button1_label`, `button1_action`, `button2_label`, `button2_action`, `dismissed`, `new`, `created`, `updated`) VALUES
(2, '6114e6e468e71', '🎉 Introducing Full Site Redirects + Headline Analyzer', 'Now easily redirect your ENTIRE website to another domain. No need to hire a developer!\r\n<br /><br />\r\nPlus, with our brand new Headline Analyzer, you can write catchy headlines that users can\'t help but click.\r\n<br /><br />\r\nTo get full access to our Pro features, upgrade your license today and save 50% OFF.', 'success', '[\"4-x\",\"lite\"]', 76, NULL, '2021-08-11 15:03:47', NULL, 'Learn More', 'https://aioseo.com/introducing-the-new-aioseo-full-site-redirect-and-headline-analyzer/?utm_source=WordPress&utm_campaign=introducing-the-new-aioseo-full-site-redirect-and-headline-analyzer-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/pricing/?utm_source=WordPress&utm_campaign=introducing-the-new-aioseo-full-site-redirect-and-headline-analyzer-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 1, 0, '2021-08-12 09:16:20', '2021-10-22 08:35:53'),
(3, '612321b13224f', '📢 Announcing HTML Sitemaps: Help Visitors Find Your Content Faster', 'Help your visitors to discover the posts, pages, and products they’re looking for.\r\n<br /><br />\r\nPlus, help improve your rankings by making your website user-friendly and easy to crawl.\r\n<br /><br />\r\nTo get full access to our Pro features, upgrade your license today and save 50% OFF. 😍', 'success', '[\"4-x\",\"lite\"]', 86, NULL, '2021-09-08 19:58:58', NULL, 'Learn More', 'https://aioseo.com/introducing-html-sitemaps/?utm_source=WordPress&utm_campaign=introducing-html-sitemaps-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/pricing/?utm_source=WordPress&utm_campaign=introducing-html-sitemaps-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 1, 0, '2021-08-23 04:18:57', '2021-10-25 12:56:27'),
(4, '614418c105eee', '📣 Introducing Local SEO Map Embeds!', 'Now it\'s easy to add multiple locations and embed a custom Google Map on your website!\r\n<br /><br />\r\nEnter your address or drop a pin on your precise location, then choose from 4 different map styles.\r\n<br /><br />\r\nPlus, get access to SEO pro tips in your editor sidebar so you can make sure each post is fully optimized.\r\n<br /><br />\r\nTo get full access to our Pro features, upgrade your license today and save 50% OFF.', 'success', '[\"4-x\",\"lite\"]', 92, NULL, '2021-09-07 00:00:00', NULL, 'Learn More', 'https://aioseo.com/announcing-local-seo-map-embeds/?utm_source=WordPress&utm_campaign=introducing-local-seo-maps-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/pricing/?utm_source=WordPress&utm_campaign=introducing-local-seo-maps-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 1, 0, '2021-09-17 04:25:37', '2021-10-25 12:56:27'),
(9, '620116b8a4a66', '📢 Introducing Link Assistant + Our Stand-Alone Headline Analyzer', 'Check out the brand new features in AIOSEO 4.1.6! First, we have Link Assistant, a powerful tool to help you build effective internal links and audit external ones.\r\n<br><br>\r\nWe’ve also added a stand-alone Headline Analyzer to help you craft, and A/B test your headlines right from within WordPress.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.6 today and up your SEO game without breaking a sweat!', 'success', '[\"4-x\",\"lite\"]', 203, NULL, '2022-01-20 20:03:16', NULL, 'Learn More', 'https://aioseo.com/new-introducing-link-assistant-a-stand-alone-headline-analyzer-and-more-in-aioseo-4-1-6/?utm_source=WordPress&utm_campaign=link-assistant-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=link-assistant-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-02-07 12:55:20', '2022-02-07 12:55:20'),
(10, '6246ab4d94974', 'Microsoft Clarity Integration and a New REST API Addon 🎉🎉', 'Now you can see how your website visitors interact with your web pages with heatmaps and session recordings, thanks to Microsoft Clarity.\r\n<br><br>\r\nWe’ve also added a REST API addon to make SEO easier for your headless website.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.9 today and enjoy these 2 powerful features on your website!', 'success', '[\"4-x\",\"lite\"]', 263, NULL, '2022-03-29 14:05:10', NULL, 'Learn More', 'https://aioseo.com/introducing-the-new-microsoft-clarity-integration-and-rest-api-addon/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=microsoft-clarity-rest-api-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-04-01 07:35:41', '2022-04-01 07:35:41'),
(11, '6246ab4d9a24c', '📢 Announcing 4.1.8: Improved Site Redirects and Modified Date for Posts 📢', 'Redirects have never been easier! Our 4.1.8 update comes with a powerful feature that enables you to implement a redirect from right within a post.\r\n<br><br>\r\nWe’ve also added a \"limit modified date\" feature that allows you to make minor edits to posts/pages and keep the date the same.\r\n<br><br>\r\nUpdate to AIOSEO 4.1.8 today and enjoy these 2 powerful features and more.', 'success', '[\"4-x\",\"lite\"]', 241, NULL, '2022-03-29 14:05:10', NULL, 'Learn More', 'https://aioseo.com/announcing-improved-site-redirects-and-modified-date-for-posts/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=introducing-on-page-redirects-and-limit-modified-date-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-04-01 07:35:41', '2022-04-01 07:35:41'),
(12, '629caa8fa19a2', '📣 Introducing Crawl Cleanup and Import URLs to Your Sitemap!', 'AIOSEO 4.2.1 now allows you to manage Search Engine crawl quota by removing query args (URL parameters) from selected URLs and disabling unnecessary RSS feeds.\r\n<br><br>\r\nYou can also import bulk URLs from a CSV file to add more pages to your sitemap.\r\n<br><br>\r\nUpdate to AIOSEO 4.2.1 to take advantage of these powerful SEO features today!', 'success', '[\"4-x\",\"lite\"]', 327, NULL, '2022-07-19 14:22:11', NULL, 'Learn More', 'https://aioseo.com/introducing-crawl-cleanup-and-importing-urls-from-a-csv-file-to-your-sitemap/?utm_source=WordPress&utm_campaign=crawl-cleanup-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/pricing/?utm_source=WordPress&utm_campaign=crawl-cleanup-v4-lite&utm_medium=plugin-notification&utm_content=Get%20AIOSEO%20Pro', 0, 0, '2022-06-05 13:07:27', '2022-07-21 00:24:27'),
(13, '629caa8fa76c9', '📢 Introducing 2 New Features! — Dashboard Widgets And Shorter URLs 📢', 'We’ve added 2 new dashboard widgets. One to help you set up your website for better SEO and the other to show you an overview of how optimized your posts are.\r\n<br><br>\r\nAnother feature you’ll love is the Strip Category Base Prefix feature that enables you to create shorter URLs by removing the category base.\r\n<br><br>\r\nUpgrade to AIOSEO 4.2.0 Pro today and enjoy these powerful features on your website!!', 'success', '[\"4-x\",\"lite\"]', 308, NULL, '2022-05-31 21:15:05', NULL, 'Learn More', 'https://aioseo.com/new-dashboard-widgets-and-strip-category-base-feature/?utm_source=WordPress&utm_campaign=dashboard-widgets-strip-category-base-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=dashboard-widgets-strip-category-base-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-06-05 13:07:27', '2022-06-14 14:09:51'),
(14, '62ca85747a45d', 'AIOSEO 4.2.2 is HERE with some great new features! 🎉', 'Connect your WordPress user profile directly to your social media accounts for improved SEO.\r\n<br><br>\r\nOur new Translations API enables third-party translation apps to hook into AIOSEO’s data for multilingual SEO.\r\n<br><br>\r\nUpdate to AIOSEO 4.2.2 now and enjoy more SEO opportunities!', 'success', '[\"4-x\",\"lite\"]', 338, NULL, '2022-07-19 14:22:11', NULL, 'Learn More', 'https://aioseo.com/announcing-our-new-user-social-profile-tab-and-translation-api-in-aioseo-4-2-2/?utm_source=WordPress&utm_campaign=social-profiles-translations-api-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=social-profiles-translations-api-v4-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-07-10 07:53:24', '2022-07-21 00:24:27'),
(15, '62d89c9d7d558', '📣 AIOSEO 4.2.3: Introducing FAQ and Table of Contents blocks + URL Slug Monitor.', 'You can now add <strong>FAQ and Table of Contents blocks</strong> to increase your visibility on SERPs and improve user experience.\r\n<br><br>\r\nAnd our <strong>URL Slug Monitor</strong> is a powerful addition that checks for changes in your URLs and prompts you to add redirects which helps <strong>prevents 404 errors</strong> on your site.\r\n<br><br>\r\nUpdate to 4.2.3 today and check out these powerful new features!', 'success', '[\"4-x\",\"lite\"]', 414, NULL, '2022-07-25 15:21:38', NULL, 'Learn More', 'https://aioseo.com/introducing-aioseo-4-2-3-faq-and-table-of-contents-blocks-all-new-url-slug-monitor/?utm_source=WordPress&utm_campaign=introducing-faq-toc-blocks-v4-lite&utm_medium=plugin-notification&utm_content=Learn More', 'Get AIOSEO Pro', 'https://aioseo.com/lite-upgrade/?utm_source=WordPress&utm_campaign=introducing-faq-toc-blocks-vv-lite&utm_medium=plugin-notification&utm_content=Get AIOSEO Pro', 0, 0, '2022-07-21 00:23:57', '2022-08-10 05:42:29');
